--   This creates the procedure to do a soft drop of a table.
-------------------------------------------------------------------------------------------------------------------------------
 
create or replace procedure &&PMF_DB_OWNER..PMF_DROP_TABLE
(
   PMF_table_name     in varchar2
)
authid current_user as
   trec               int;
 
begin
 
   select count(*) into trec
   from ALL_TRIGGERS
   where OWNER         = '&&PMF_DB_OWNER'
     and TRIGGER_NAME  = PMF_table_name || '_TR';
 
   if (trec > 0) then
      execute immediate 'drop trigger '  || '&&PMF_DB_OWNER' || '.' || PMF_table_name || '_TR' ;
   end if;
 
   select count(*) into trec
   from ALL_SEQUENCES
   where SEQUENCE_OWNER = '&&PMF_DB_OWNER'
     and SEQUENCE_NAME  = PMF_table_name || '_SQ';
 
   if (trec > 0) then
      execute immediate 'drop sequence ' || '&&PMF_DB_OWNER' || '.' || PMF_table_name || '_SQ' ;
   end if;
 
   select count(*) into trec
   from ALL_SYNONYMS
   where TABLE_OWNER  = '&&PMF_DB_OWNER'
     and SYNONYM_NAME = PMF_table_name ;
 
   if (trec > 0) then
      execute immediate 'drop public synonym ' || PMF_table_name ;
   end if;
 
   select count(*) into trec
   from ALL_TABLES
   where OWNER         = '&&PMF_DB_OWNER'
     and TABLE_NAME    = PMF_table_name ;
 
   if (trec > 0) then
      execute immediate 'drop table '    || '&&PMF_DB_OWNER' || '.' || PMF_table_name || ' cascade constraints' ;
   end if;
 
exception
   when others then
       null;
 
end PMF_DROP_TABLE;
/
 
--  NOTE: You will get an 'Object does not exists' message on the first run of this script for the drop public synonyms on the procedures.
--        This is OK.
drop   public synonym PMF_DROP_TABLE ;
create public synonym PMF_DROP_TABLE for &&PMF_DB_OWNER..PMF_DROP_TABLE
/
 
 
