--  This procedure helps configure the Oracle equivalent of the MSSQL & DB2 identity columns
-------------------------------------------------------------------------------------------------------------------------------
 
create or replace procedure &&PMF_DB_OWNER..PMF_SEQUENCE_TRIGGER_CREATE
(
   pmf_table_name     in varchar2,
   pmf_column_name    in varchar2
)
authid current_user as
   trec               int;
 
begin
   select count(*) into trec
   from ALL_SEQUENCES
   where SEQUENCE_OWNER = '&&PMF_DB_OWNER'
     and SEQUENCE_NAME  = pmf_table_name  || '_SQ';
 
   if (trec > 0) then
      execute immediate 'drop sequence ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_SQ' ;
   end if;
 
   execute immediate 'create sequence '            || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_SQ' ;
 
   execute immediate 'create  or replace trigger ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_TR'
                 || ' before insert on '           || '&&PMF_DB_OWNER' || '.' || pmf_table_name
                 || ' for each row '
                 || '    begin '
                 || '      select ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_SQ.nextval into :new.' || pmf_column_name || ' from dual;'
                 || '    end; ' ;
 
   execute immediate 'select '          || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_SQ.nextval from dual ' ;
 
exception
   when others then
       null;
 
end PMF_SEQUENCE_TRIGGER_CREATE;
/
 
drop   public synonym PMF_SEQUENCE_TRIGGER_CREATE ;
create public synonym PMF_SEQUENCE_TRIGGER_CREATE for &&PMF_DB_OWNER..PMF_SEQUENCE_TRIGGER_CREATE
/
 
-------------------------------------------------------------------------------------------------------------------------------
--  This routine sets up the Oracle equivalent of the MSSQL & DB2 identity columns for a pre-existing TABLE that may have data
--  It differs from sqlora_PMF_sequence_trigger_create in that the sequence may start at a non zero value
-------------------------------------------------------------------------------------------------------------------------------
 
create or replace procedure &&PMF_DB_OWNER..PMF_SEQUENCE_TRIGGER_RESET
(
   pmf_table_name     in varchar2,
   pmf_column_name    in varchar2
)
authid current_user as
   trec               int;
   next_seq_num       int;
 
begin
   select count(*) into trec
   from ALL_SEQUENCES
   where SEQUENCE_OWNER = '&&PMF_DB_OWNER'
     and SEQUENCE_NAME  = pmf_table_name  || '_SQ';
 
   if (trec > 0) then
      execute immediate 'drop sequence ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_SQ ' ;
   end if;
 
   execute immediate 'select count(*) + 1 from ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name into next_seq_num  ;
 
   if (next_seq_num > 1) then
      execute immediate 'select max(' || pmf_column_name || ') + 1 from ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name into next_seq_num  ;
   end if;
 
   execute immediate 'create sequence ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_SQ minvalue ' ||  next_seq_num ;
 
   execute immediate 'create  or replace trigger ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_TR'
                 || ' before insert on '           || '&&PMF_DB_OWNER' || '.' || pmf_table_name
                 || ' for each row '
                 || '    begin '
                 || '      select ' || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_SQ.nextval into :new.' || pmf_column_name || ' from dual;'
                 || '    end; ' ;
 
   execute immediate 'select '          || '&&PMF_DB_OWNER' || '.' || pmf_table_name || '_SQ.nextval from dual ' ;
 
exception
   when others then
       null;
 
end PMF_SEQUENCE_TRIGGER_RESET;
/
 
drop   public synonym PMF_SEQUENCE_TRIGGER_RESET ;
create public synonym PMF_SEQUENCE_TRIGGER_RESET for &&PMF_DB_OWNER..PMF_SEQUENCE_TRIGGER_RESET
/
 
 
 
 
