--  There are variations in the DDL due to differences in handling Date-Time arithmetic between the supported RDBMSs.
--  We need to subtract 2 milliseonds in the DDL for SQL Server due to anomolies in the behavior of SQL Server 2005.
 
--  , coalesce(DATEADD(ms, -2, new1.LOAD_TIME_START),                       '9999-12-31 23:59:59.998')                                as "NEXT_LOAD_TIME_START" -- SQLMSS
--  , coalesce(SUBTIME(new1.LOAD_TIME_START,'00:00:00.001'),                '9999-12-31 23:59:59.999')                                as "NEXT_LOAD_TIME_START" -- SQLMYSQL
--  , coalesce(new1.LOAD_TIME_START - interval '0.001' second, to_timestamp('9999-12-31 23:59:59.999', "YYYY-MM-DD HH24:MI:SS.FF3"))  as "NEXT_LOAD_TIME_START" -- SQLORA
--  , coalesce(new1.LOAD_TIME_START - 100 microsecond,                      '9999-12-31 23:59:59.999')                                as "NEXT_LOAD_TIME_START" -- DB2
--  , coalesce(new1.LOAD_TIME_START - interval '0.001' second,         cast('9999-12-31 23:59:59.999') as timestamp)                  as "NEXT_LOAD_TIME_START" -- Teradata
--  , coalesce(new1.LOAD_TIME_START - interval '0.001' second,              '9999-12-31 23:59:59.999')                                as "NEXT_LOAD_TIME_START" -- SQLPSTGRE
 
